INSERT INTO `ci_post_news` VALUES (18, '2', '2022-06-17 08:00:00', '2022-06-17 08:00:00', '2', 'Y', 'N', '', 'Pengumuman Jadwal Semester Pendek Periode Juni 2022', '&lt;p&gt;Berikut lampiran jadwal Semester Pendek (SP) periode&amp;nbsp;Juni 2022 yang dilaksanakan secara Offline (Tatap Muka). Bagi mahasiswa yang sudah mendaftar tetapi tidak tercantum atau terdaftar namanya &amp;nbsp;mohon konfirmasi ke bagian BAAK dengan membawa Surat permohonan pendaftaran Semester Pendek (SP) dan bukti Kwitansi atau slip Pembayaran. terima kasih.&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;MASTER_SP_SIANG_JUNI&quot; href=&quot;https://www.trigunadharma.ac.id/media/uploads/Mg%3D%3D/2022/MASTER_SP_SIANG_JUNI.xlsx&quot;&gt;MASTER_SP_SIANG_JUNI&lt;/a&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;MASTER_SP_SORE_JUNI&quot; href=&quot;https://www.trigunadharma.ac.id/media/uploads/Mg%3D%3D/2022/MASTER_SP_SORE_JUNI.xlsx&quot;&gt;MASTER_SP_SORE_JUNI&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;#TATAP MUKA (OFFLINE)&lt;/p&gt;\r\n&lt;p&gt;#download dan periksa daftar nama per sheet.&lt;/p&gt;\r\n&lt;p&gt;#kepada mahasiswa/i yang sudah keluar jadwal semester pendek nya dapat menghubungi dosen yang sudah ditentukan 1 hari sebelum pelaksanaan berlangsung.&lt;/p&gt;\r\n&lt;p&gt;#&lt;strong&gt;apabila jadwal semester pendek terlewat pada jadwal yang sudah ditentukan berarti terhitung hangus dan mahasiswa harus mendaftar dan membayar kembali.&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;');
