INSERT INTO `ci_post_news` VALUES (25, '2', '2022-07-21 09:00:00', '2022-07-21 09:00:00', '2', 'Y', 'N', '', 'INFORMASI JADWAL PENDAFTARAN DAN PELAKSANAAN SEMINAR DAN SIDANG SKRIPSI DAN TUGAS AKHIR REGULER  TAHUN 2022', '&lt;p&gt;Diinformasikan kepada Seluruh Mahasiswa/i Tingkat Akhir yang menyusun Skripsi maupun Tugas Akhir. berikut ini lampiran jadwal pendaftaran Seminar dan Sidang Mahasiswa tingkat akhir Tahun 2022.&lt;/p&gt;\r\n&lt;p&gt;Untuk pendaftaran dimulai pada tanggal 17 Desember 2022 dan pelaksanaan seminar perdananya pada tanggal 15 Januari 2022&lt;/p&gt;\r\n&lt;p&gt;#untuk jam pendaftaran disesuaikan dengan jam pelayanan di BAAK.&lt;/p&gt;\r\n&lt;p&gt;1.Syarat Pendaftaran :&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;SYARAT_SEMINAR_PROPOSAL_HASIL_DAN_SIDANG_2022_&quot; href=&quot;https://www.trigunadharma.ac.id/media/uploads/Mg%3D%3D/2022/SYARAT_SEMINAR_PROPOSAL_HASIL_DAN_SIDANG_2022_.docx&quot;&gt;SYARAT_SEMINAR_PROPOSAL_HASIL_DAN_SIDANG_2022_&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;2.Biaya Administrasi Seminar Proposal, Hasil, Sidang dan Wisuda.&lt;/p&gt;\r\n&lt;p&gt;&lt;a title=&quot;Biaya_SEMINAR_SIDANG_2022&quot; href=&quot;https://www.trigunadharma.ac.id/media/uploads/Mg%3D%3D/2021/Biaya_SEMINAR_SIDANG_2022.pdf&quot;&gt;Biaya_SEMINAR_SIDANG_2022&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;#Bagi mahasiswa cuti yang melewati batas konversi dan mahasiswa telat studi untuk mengacu biaya seminar dan sidang jadwal 4 tahun (2021).&lt;/p&gt;\r\n&lt;p&gt;3. Jadwal dan Pelaksanaan.&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;https://www.trigunadharma.ac.id/media/uploads/Mg%3D%3D/2021/Tahap_1_2022.jpg&quot; alt=&quot;Tahap_1_2022&quot; /&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;img src=&quot;https://www.trigunadharma.ac.id/media/uploads/Mg%3D%3D/2021/Tahap_2_2022.jpg&quot; alt=&quot;Tahap_2_2022&quot; /&gt;&lt;/p&gt;');
