INSERT INTO `ci_post_news` VALUES (35, '1', '2022-01-10 04:20:32', '2022-01-10 04:20:32', '5', 'Y', 'N', 'alumni-jenjang-perkuliahan_210220232945-392-26.06.2021.13.21.11_1.jpg', 'Pengumuman Validasi Mahasiswa Penerima Bantuan UKT/SPP 2021/2022 Ganjil Tahap I &amp; Tahap II', '&lt;p&gt;Sehubungan dengan Surat Kepala LLDIKTI Nomor 221 Tahun 2021 tentang penyampaian Laporan Pencairan Dana Bantuan UKT/SPP Skema Reguler, maka dengan ini kami informasikan beberapa hal sebagai berikut :&lt;/p&gt;\r\n&lt;ol&gt;\r\n&lt;li&gt;Mahasiswa Penerima Bantuan UKT/SPP Ganjil 2021/2022.&lt;/li&gt;\r\n&lt;li&gt;Kepada Mahasiswa tersebut untuk segera membayar sisa uang kuliah yang telah di potong Bantuan UKT/SPP paling lama Tanggal 26 Januari 2022.&lt;/li&gt;\r\n&lt;li&gt;Harap Melapor ke Bag. Keuangan dan Kemahasiswaan untuk Validasi Pembayaran UKT pada jadwal berikut :\r\n&lt;ul&gt;\r\n&lt;li&gt;Untuk Mahasiswa No urut 1 s.d 100 Melapor Pada Tanggal 20 Januari 2022.&lt;/li&gt;\r\n&lt;li&gt;Untuk Mahasiswa No urut 101 s.d 200 Melapor Pada Tanggal 21 Januari 2022.&lt;/li&gt;\r\n&lt;li&gt;Untuk Mahasiswa No urut 201 s.d 300 Melapor Pada Tanggal 24 Januari 2022.&lt;/li&gt;\r\n&lt;li&gt;Untuk Mahasiswa No urut 301 s.d 400 Melapor Pada Tanggal 25 Januari 2022.&lt;/li&gt;\r\n&lt;li&gt;Untuk Mahasiswa No urut 401 s.d 506 Melapor Pada Tanggal 26 Januari 2022.&amp;nbsp;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;/li&gt;\r\n&lt;li&gt;Tempat / Waktu&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;:&amp;nbsp; Aula lt.4 Pukul, 08.00.wib s.d Selesai.&lt;/li&gt;\r\n&lt;li&gt;Penerima Bantuan UKT Adalah Mahasiswa Aktif.&lt;/li&gt;\r\n&lt;/ol&gt;\r\n&lt;p&gt;Diharapkan kepada mahasiswa penerima bantuan UKT/SPP 2021/2022 Ganjil untuk dapat hadir mem Validasi&amp;nbsp;dan melakukan pembayaran sisa potongan uang kuliah sesuai dengan jadwal yang telah di berikan. apabila tidak hadir sesuai dengan jadwal maka di anggap mengundurkan diri.&lt;/p&gt;\r\n&lt;p&gt;untuk informasi lebih lanjut dapat bertanya langsung kepada staff Bidang Kemahasiswaan.&lt;/p&gt;\r\n&lt;p&gt;Demikian Pemberitahuan dari kami, atas perhatian dan kerjasama yang baik diucapkan terima kasih.&lt;/p&gt;\r\n&lt;p&gt;Lampiran Surat&amp;nbsp; :&amp;nbsp;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;em&gt;&lt;strong&gt;&lt;a title=&quot;DATA_MAHASISWA_PENERIMA_BANTUAN_UKT&quot; href=&quot;https://www.trigunadharma.ac.id/media/uploads/NQ%3D%3D/skpi/DATA_MAHASISWA_PENERIMA_BANTUAN_UKT.pdf&quot;&gt;DATA_MAHASISWA_PENERIMA_BANTUAN_UKT&lt;/a&gt;&lt;/strong&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;');
