INSERT INTO `ci_post_news` VALUES (67, '2', '2022-02-11 11:30:46', '2022-02-11 11:30:46', '4', 'Y', 'N', '', 'PENDAMPING PROGRAM KREATIVITAS MAHASISWA PKM PRODI SISTEM INFORMASI TAHAP 2', '&lt;p&gt;&lt;span style=&quot;text-decoration: underline; font-size: 18px;&quot;&gt;&lt;strong&gt;Budayakan Membaca Terlebih Dahulu Sebelum Mengunduh.&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;Diberitahukan Kpd Mahasiswa/i Program Studi Sistem Informasi yang sudah mengajukan Judul PKM&amp;nbsp; oleh Kaprodi bahwasanya SK Dosen Pendamping PKM Tahap 02 sudah dapat di unduh:&lt;/p&gt;\r\n&lt;p&gt;1. Cetak SK Dosen pendamping dengan kertas A4 dan diprint warna&lt;br /&gt;2. Sk Dosen Pendamping dicetak 2 lembar yang isinya:&lt;br /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; a. Surat Keputusan (SK)&lt;br /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; b. Lampiran ( Yang dicetak hanya nama yang bersangkutan)&lt;br /&gt;3. Validasi dibagian Baak&amp;nbsp;&lt;br /&gt;4. Dibagian lampiran stabilo nama yang bersangkutan&lt;br /&gt;5. menyerahkan SK dosen pendamping dan lampiran ke dosen yang dihunjuk.&lt;/p&gt;\r\n&lt;p&gt;&lt;a href=&quot;https://www.trigunadharma.ac.id/media/uploads/NA%3D%3D/Upload_92.rar&quot;&gt;SK PENDAMPING PKM SISTEM INFOFRMASI TAHAP 2&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;Nb. SK Dosen Pendamping dicetak sesuai jumlah anggota dan ditambah 1 rangkap untuk dosen pendamping, 1&amp;nbsp;rangkap untuk Dosen&amp;nbsp;pendamping,&amp;nbsp;selebihnya untuk pertinggal mahasiswa.&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;TerimakasihStaffProdi&lt;/span&gt;&lt;/p&gt;');
