INSERT INTO `ci_post_news` VALUES (88, '2', '2022-03-04 10:07:00', '2022-03-04 10:07:00', '4', 'Y', 'N', '', 'JADWAL PEMINATAN PROGRAM PROGRAM STUDI SISTEM INFORMASI DAN SISTEM KOMPUTER', '&lt;p&gt;Kepada mahasiswa semester 8 dan sedang menjalani penyusunan skripsi, berikut jadwal peminatan program&lt;/p&gt;\r\n&lt;p&gt;&lt;a href=&quot;https://www.trigunadharma.ac.id/media/uploads/NA%3D%3D/Peminatan_Program205.pdf&quot;&gt;JADWAL PEMINATAN PROGRAM&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;dan berikut kelas peminatan program sesuai bidang yang telah dipilih.&lt;/p&gt;\r\n&lt;p&gt;&lt;a href=&quot;https://www.trigunadharma.ac.id/media/uploads/NA%3D%3D/Kelas_Peminatan_Program.xlsx&quot;&gt;KELAS PEMINATAN PROGRAM&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;');
INSERT INTO `ci_post_news` VALUES (89, '2', '2022-03-04 03:33:33', '2022-03-04 03:33:33', '4', 'Y', 'N', '', 'PENDAMPING PROGRAM KREATIVITAS MAHASISWA PKM PRODI SISTEM INFORMASI TAHAP 3', '&lt;p&gt;&lt;strong&gt;Budayakan Membaca Terlebih Dahulu Sebelum Mengunduh&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Diberitahukan Kpd Mahasiswa/i Program Studi Sistem Informasi yang sudah mengajukan Judul PKM&amp;nbsp; oleh Kaprodi bahwasanya SK Dosen Pendamping PKM Tahap 03 sudah dapat di unduh:&lt;/p&gt;\r\n&lt;p&gt;1. Cetak SK Dosen pendamping dengan kertas A4 dan diprint warna&lt;br /&gt;2. Sk Dosen Pendamping dicetak 2 lembar yang isinya:&lt;br /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; a. Surat Keputusan (SK)&lt;br /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; b. Lampiran ( Yang dicetak hanya nama yang bersangkutan)&lt;br /&gt;3. Validasi dibagian Baak&amp;nbsp;&lt;br /&gt;4. Dibagian lampiran stabilo nama yang bersangkutan&lt;br /&gt;5. menyerahkan SK dosen pendamping dan lampiran ke dosen yang dihunjuk.&lt;/p&gt;\r\n&lt;p&gt;&lt;a href=&quot;https://www.trigunadharma.ac.id/media/uploads/NA%3D%3D/SK_PEMBIMBING_71.rar&quot;&gt;SK PENDAMPING PKM SISTEM INFORMASI TAHAP 3&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;Nb. SK Dosen Pendamping dicetak sesuai jumlah anggota dan ditambah 1 rangkap untuk dosen pendamping, 1&amp;nbsp;rangkap untuk Dosen&amp;nbsp;pendamping,&amp;nbsp;selebihnya untuk pertinggal mahasiswa.&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;em&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&lt;strong&gt;TerimakasihStaffProdi&lt;/strong&gt;&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;');
INSERT INTO `ci_post_news` VALUES (90, '2', '2022-03-04 03:44:58', '2022-03-04 03:44:58', '4', 'Y', 'N', '', 'PENDAMPING PROGRAM KREATIVITAS MAHASISWA PKM PRODI SISTEM KOMPUTER TAHAP 3', '&lt;p&gt;&lt;strong&gt;Budayakan Membaca Terlebih Dahulu Sebelum Mengunduh&lt;/strong&gt;&lt;/p&gt;\r\n&lt;p&gt;Diberitahukan Kpd Mahasiswa/i Program Studi Sistem Komputer yang sudah mengajukan Judul PKM&amp;nbsp; oleh Kaprodi bahwasanya SK Dosen Pendamping PKM Tahap 03 sudah dapat di unduh:&lt;/p&gt;\r\n&lt;p&gt;1. Cetak SK Dosen pendamping dengan kertas A4 dan diprint warna&lt;br /&gt;2. Sk Dosen Pendamping dicetak 2 lembar yang isinya:&lt;br /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; a. Surat Keputusan (SK)&lt;br /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; b. Lampiran ( Yang dicetak hanya nama yang bersangkutan)&lt;br /&gt;3. Validasi dibagian Baak&amp;nbsp;&lt;br /&gt;4. Dibagian lampiran stabilo nama yang bersangkutan&lt;br /&gt;5. menyerahkan SK dosen pendamping dan lampiran ke dosen yang dihunjuk.&lt;/p&gt;\r\n&lt;p&gt;&lt;a href=&quot;https://www.trigunadharma.ac.id/media/uploads/NA%3D%3D/SK_PEMBIMBING_73.rar&quot;&gt;SK PENDAMPING PKM SISTEM KOMPUTER TAHAP 3&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;Nb. SK Dosen Pendamping dicetak sesuai jumlah anggota dan ditambah 1 rangkap untuk dosen pendamping, 1&amp;nbsp;rangkap untuk Dosen&amp;nbsp;pendamping,&amp;nbsp;selebihnya untuk pertinggal mahasiswa.&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;em&gt;&lt;span style=&quot;text-decoration: underline;&quot;&gt;&lt;strong&gt;TerimakasihStaffProdi&lt;/strong&gt;&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;');
