INSERT INTO `ci_post_news` VALUES (155, '2', '2022-06-22 12:19:18', '2022-06-22 12:19:18', '13', 'Y', 'N', '', 'SURAT KETERANGAN SIDANG UNTUK D3', '&lt;p&gt;Kepada seluruh mahasiswa Diploma 3 yang sedang menyusun Tugas Akhir dan akan melaksanakan Sidang,&amp;nbsp;untuk mendapatkan Surat Keterangan Pendaftaran Sidang dari PRPM&amp;nbsp; maka diharapkan dapat melengkapi berkas seperti pada lampiran dibawah ini.&lt;/p&gt;\r\n&lt;p&gt;Klik link berikut : &lt;a title=&quot;BERKAS_D3&quot; href=&quot;https://www.trigunadharma.ac.id/media/uploads/MTM%3D/BERKAS_D3.rar&quot;&gt;BERKAS_D3&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;Penyerahan berkas diantar langsung ke Ruangan PRPM. sedangkan untuk Surat Keterangan Pendaftaran Sidang akan dikirimkan melalui email.&lt;/p&gt;\r\n&lt;p&gt;Harap diperhatikan syarat yang dibutuhkan karena ada yang bersifat Hardcopy dan Softcopy serta disarankan membuka file lampiran tersebut menggunakan komputer (jangan dari HP).&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;Demikian informasi ini disampaikan.&lt;/p&gt;\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;ttd&lt;/p&gt;\r\n&lt;p&gt;PRPM&lt;/p&gt;');
